    <script>
        $(document).ready(function() {
            var token = $('input[name=_token]').val();
            $.ajaxSetup({
                headers: {
                    'X-CSRF-TOKEN': token
                }
            });

            $('.logout').click(function() {
                $.ajax({
                    url: '/logout',
                    type: 'post',
                    data: {
                        url: window.location.pathname
                    },
                    error: function(err) {
                        console.log(err);
                    },
                    success: function(data) {
                        console.log(data);
                        window.location.href = data.redirect;
                    }
                });
                return false;
            });
        });
    </script>

